import React, { useEffect, useState } from 'react';
import { DateTime } from 'luxon';
import '../css/Header.css';
import '../style.scss';

const Header = () => {
  const [formattedDate, setFormattedDate] = useState('');
  const [isOpen, setIsOpen] = useState(false);

  const onClick = () => {
    setIsOpen(!isOpen);
  };

  const handleServiceClick = () => {
    // Traitez la navigation ou l'action pour le lien "Services"
    // Vous pouvez utiliser react-router-dom ou une autre méthode de navigation
    console.log('Services clicked');
    setIsOpen(false); // Fermer le menu après le clic
  };

  const handleAdminClick = () => {
    // Traitez la navigation ou l'action pour le lien "Administrateur"
    // Vous pouvez utiliser react-router-dom ou une autre méthode de navigation
    console.log('Administrateur clicked');
    setIsOpen(false); // Fermer le menu après le clic
  };

  useEffect(() => {
    // Obtenir la date actuelle avec Luxon
    const now = DateTime.local();

    // Formater la date en utilisant toLocaleString
    const formatted = now.toLocaleString({
      weekday: 'long', // Jour de la semaine (complet)
      day: 'numeric', // Jour du mois
      month: 'short', // Mois (abrégé)
    });

    // Mettre à jour l'état avec la date formatée
    setFormattedDate(formatted);
  }, []);

  return (
    <div className="header">
      <h1 className="date">{formattedDate}</h1>
      <div className={`hamburger-menu ${isOpen ? 'open' : ''}`} onClick={onClick}>
        <span className="line line-1"></span>
        <span className="line line-2"></span>
      </div>
      {isOpen && (
        <div className="menu">
          <button onClick={handleServiceClick}>Services</button>
          <button onClick={handleAdminClick}>Administrateur</button>
        </div>
      )}
    </div>
  );
};

export default Header;
