import React, { useEffect, useState } from 'react';
import { DateTime } from 'luxon';
import '../index.scss';
import CheckboxComponents from './checkbox/checkbox';

const Header = ({ onMenuToggle }) => {
  const [formattedDate, setFormattedDate] = useState('');
  const [isOpen, setIsOpen] = useState(false);

  const onClick = () => {
    setIsOpen(!isOpen);
  };

  useEffect(() => {
    const blurElement = document.querySelector('.blur');
    const menuButton = document.querySelector('.hamburger-menu');

    const handleClick = () => {
      blurElement.classList.toggle('open', isOpen);
    };

    menuButton.addEventListener('click', handleClick);

    return () => {
      menuButton.removeEventListener('click', handleClick);
    };
  }, [isOpen]);

  useEffect(() => {
    const now = DateTime.local();

    const formatted = now.toLocaleString({
      weekday: 'long',
      day: 'numeric',
      month: 'short',
    });

    setFormattedDate(formatted);
  }, []);

  return (
    <div className="header">
      <div className={`hamburger-menu ${isOpen ? 'open' : ''}`} onClick={onClick}>
        <span className="line line-1"></span>
        <span className="line line-2"></span>
      </div>
      {isOpen && (
        <div className="menu">
          <div className="lien">
            <a className="Service">
              <svg width="31" height="26" viewBox="0 0 31 26" fill="none" xmlns="http://www.w3.org/2000/svg">
                {/* ... (votre contenu SVG) */}
              </svg>
              Service
            </a>
            <a className="administrateur">
              <svg width="31" height="25" viewBox="0 0 31 25" fill="none" xmlns="http://www.w3.org/2000/svg">
                {/* ... (votre contenu SVG) */}
              </svg>
              Administrateur
            </a>
          </div>
          <a href="#" className="close_service">
            Fermer le service
            <svg width="22" height="22" viewBox="0 0 22 22" fill="none" xmlns="http://www.w3.org/2000/svg">
              {/* ... (votre contenu SVG) */}
            </svg>
          </a>
        </div>
      )}
      <div className={`blur ${isOpen ? 'open' : ''}`}>
        <h1 className="date">{formattedDate}</h1>
        <h1>Titre page</h1>
      </div>
      <CheckboxComponents isOpen={isOpen} />
    </div>
  );
};

export default Header;
