// components/checkbox/checbox.js

import React, { useEffect, useState } from 'react';
import '../../style.scss';  // Corrected import path for style
import Photo from '../../img/profil.svg';

const CheckboxComponents = ({ isOpen }) => {  // Added isOpen prop

  useEffect(() => {
    const blurElement = document.querySelector('.checkbox-container');
    const menuButton = document.querySelector('.hamburger-menu');

    const handleClick = () => {
      blurElement.classList.toggle('open', isOpen);
    };

    menuButton.addEventListener('click', handleClick);

    return () => {
      menuButton.removeEventListener('click', handleClick);
    };
  }, [isOpen]);

  return (
    <div className={`checkbox-container ${isOpen ? 'blur' : ''}`}>
      <ul>
        <li><img src={Photo} alt="Profile"></img><p>Aaron James</p><input type="checkbox" /></li>
        <li><img src={Photo} alt="Profile"></img><p>Amanda Wallace</p><input type="checkbox" /></li>
        <li><img src={Photo} alt="Profile"></img><p>Angela Danger</p><input type="checkbox" /></li>
        <li><img src={Photo} alt="Profile"></img><p>Abella white</p><input type="checkbox" /></li>
        <li><img src={Photo} alt="Profile"></img><p>Darlene Robertsone</p><input type="checkbox" /></li>
        <li><img src={Photo} alt="Profile"></img><p>Fred Davis</p><input type="checkbox" /></li>
        <li><img src={Photo} alt="Profile"></img><p>Aaron Smith</p><input type="checkbox" /></li>
        <li><img src={Photo} alt="Profile"></img><p>Aaron Smith</p><input type="checkbox" /></li>
      </ul>
    </div>
  );
}

export default CheckboxComponents;
