import React, { useEffect, useState } from 'react';
import { DateTime } from 'luxon';

const Header = () => {
  const [formattedDate, setFormattedDate] = useState('');

  useEffect(() => {
    // Obtenir la date actuelle avec Luxon
    const now = DateTime.local();

    // Formater la date en utilisant toLocaleString
    const formatted = now.toLocaleString({
      weekday: 'long', // Jour de la semaine (complet)
      day: 'numeric', // Jour du mois
      month: 'short', // Mois (abrégé)
    });

    // Mettre à jour l'état avec la date formatée
    setFormattedDate(formatted);
  }, []);

  return (
    <div className="header">
      <h1>{formattedDate}</h1>
      {/* Ajoutez ici d'autres éléments de votre header, tels que le bouton */}
      {/* <Link to="/service">
        <button>Aller à Service</button>
      </Link> */}
    </div>
  );
};

export default Header;
