import React, { useState } from 'react';
import style from '../../style.scss';
import Photo from '../../img/profil.svg';

const CheckboxComponents = () => {
  const [selectedOption, setSelectedOption] = useState('');

  const handleChange = (event) => {
    setSelectedOption(event.target.value);
  };

  return (
    <div className={`checkbox-container ${style['checkbox-container']}`}>
      {/* Barre déroulante */}
      <select value={selectedOption} onChange={handleChange}>
        <option value="">Sélectionnez une option</option>
        <option value="Aaron James">Aaron James</option>
        <option value="Amanda Wallace">Amanda Wallace</option>
        <option value="Angela Danger">Angela Danger</option>
        <option value="Abella white">Abella white</option>
        <option value="Darlene Robertsone">Darlene Robertsone</option>
        <option value="Fred Davis">Fred Davis</option>
        <option value="Aaron Smith">Aaron Smith</option>
        <option value="Aaron Smith">Aaron Smith</option>
      </select>

      {/* Affichage de la sélection */}
      {selectedOption && (
        <div>
          <img src={Photo} alt="Profile" />
          <p>{selectedOption}</p>
          <input type="checkbox" />
        </div>
      )}
    </div>
  );
}

export default CheckboxComponents;
